export default function babysitterReducer(state = { babysitters: [], loading: false }, action) {

    switch (action.type) {
        case 'LOADING_BABYSITTERS':
            return {
                ...state,
                babysitters: [...state.babysitters],
                loading: true
            }

        case 'FETCH_BABYSITTERS':
            console.log("action.payload", action.payload)
            return { babysitters: action.payload, loading: false }


        case 'ADD_BABYSITTER':
            return {
                ...state,
                babysitters: [...state.babysitters, action.payload],
                loading: false
            }

        case 'ADD_COMMENT':
            return {
                ...state, babysitters: state.babysitters.map(baby => {
                    if (baby.id === action.payload.id) {
                        return action.payload
                    } else { return baby }
                })
            }

        case 'DELETE_COMMENT':
            return {
                ...state, babysitters: state.babysitters.map(baby => {
                    if (baby.id === action.payload.id) {
                        return action.payload
                    } else { return baby }
                })
            }

        case 'ADD_SLOT': 
        let sitter = state.babysitters.filter(babysitters.id === action.payload.id)
            //  => {
            //      (baby.id === action.payload.id) {
            //         return baby
            //     } 
            // })
            console.log("sitter", sitter)
            return { ...state, babysitters: sitter }



        default:
            return state


    }
}
