import React from 'react'
import {connect} from 'react-redux'
import {deleteComment} from '../actions/deleteComment'

const Comments = (props) => {

    const handleDelete = (commentId, babysitterId) => {
        props.deleteComment()
    }

    return (
        <div>
            {props.comments && props.comments.map(comment =>
                <li key={comment.id}>{comment.message} - 
                    <i>{comment.name ? comment.name : null} </i>
                    <br/><button onClick={() => handleDelete(comment.id, comment.babysitter.id)}>Delete</button>    
                </li>
                
            )}
        </div>
    )

}

export default connect(null, {deleteComment})(Comments)
