export const getBabysitters = (props) => {
    return { type: 'FETCH_BABYSITTERS', payload: props }
}

export const slotMerge = babyData => {
    // return console.log("babyData test", babyData[0].slots)
    const slots = babyData.map(babysitters => babysitters.slots)
    const slot = slots.map(slot => console.log("testing #2", slot))
