import React from 'react'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import CardDeck from 'react-bootstrap/CardDeck'
import {Link} from 'react-router-dom'
import ListGroup from 'react-bootstrap/ListGroup'

const Slots = (props) => {
    console.log("this is props", props)

    function dayToString(day_of_week){
        if (day_of_week === 0){
            return "Sunday"
        }else if (day_of_week === 1){
            return "Monday"
        }else if (day_of_week === 2){
            return "Tuesday"
        }else if (day_of_week === 3){
            return "Wednesday"
        }else if (day_of_week === 4){
            return "Thursday"
        }else if (day_of_week == 5){
            return "Friday"
        }else if (day_of_week === 6){
            return "Saturday"
        }
    }

    function timeToString(time_of_day){
        if (time_of_day === 0){
            return "Morning"
        }else if (time_of_day === 1){
            return "Afternoon"
        }else if (time_of_day === 2){
            return "Evening"
        }

    }

    return (
        <div>
            {/* idea #1 */}
            {/* Sunday
            {props.slots && props.slots.map(slot =>
                {if (slot.day_of_week === 0) {
                return(
                    <Card style={{ width: '18rem' }}>
                    <Card.Header>Sunday</Card.Header>
                    <ListGroup variant="info">
                    <ListGroup.Item>
                    {(timeToString(slot.time_of_day))}
                        </ListGroup.Item>
                    
                </ListGroup>
                </Card>
                )
             }}
             )}
            {/* Monday */}
            {props.slots && props.slots.map(slot =>
                {if (slot.day_of_week === 1) {
                return(
                    <Card style={{ width: '18rem' }}>
                    <Card.Header>Monday</Card.Header>
                    <ListGroup variant="info">
                    <ListGroup.Item>
                    {(timeToString(slot.time_of_day))}
                        </ListGroup.Item>
                    
                </ListGroup>
                </Card>
                )
             }}
             )}
        {/* Tuesday */}
            {props.slots && props.slots.map(slot =>
                {if (slot.day_of_week === 2) {
                return(
                    <Card style={{ width: '18rem' }}>
                    <Card.Header>Tueday</Card.Header>
                    <ListGroup variant="info">
                    <ListGroup.Item>
                    {(timeToString(slot.time_of_day))}
                        </ListGroup.Item>
                    
                </ListGroup>
                </Card>
                )
            }}
            )}

        {/* Wednesday */}
        {props.slots && props.slots.map(slot =>
                {if (slot.day_of_week === 3) {
                return(
                    <Card style={{ width: '18rem' }}>
                    <Card.Header>Wednesday</Card.Header>
                    <ListGroup variant="info">
                    <ListGroup.Item>
                    {(timeToString(slot.time_of_day))}
                        </ListGroup.Item>
                    
                </ListGroup>
                </Card>
                )
             }}
             )}
        {/* Thursday */}
        {props.slots && props.slots.map(slot =>
                {if (slot.day_of_week === 4) {
                return(
                    <Card style={{ width: '18rem' }}>
                    <Card.Header>Thursday</Card.Header>
                    <ListGroup variant="info">
                    <ListGroup.Item>
                    {(timeToString(slot.time_of_day))}
                        </ListGroup.Item>
                    
                </ListGroup>
                </Card>
                )
             }}
             )}
        {/* Friday */}
        {props.slots && props.slots.map(slot =>
                {if (slot.day_of_week === 5) {
                return(
                    <Card style={{ width: '18rem' }}>
                    <Card.Header>Friday</Card.Header>
                    <ListGroup variant="info">
                    <ListGroup.Item>
                    {(timeToString(slot.time_of_day))}
                        </ListGroup.Item>
                </ListGroup>
                </Card>
                )
             }}
             )}
        {/* Saturday */}
        {props.slots && props.slots.map(slot =>
                {if (slot.day_of_week === 6) {
                return(
                    <Card style={{ width: '18rem' }}>
                    <Card.Header>Saturday</Card.Header>
                    <ListGroup variant="info">
                    <ListGroup.Item>
                    {(timeToString(slot.time_of_day))}
                        </ListGroup.Item>
                    
                </ListGroup>
                </Card>
                )
             }}
             )} */}
             {/* end of idea #1 */}

             {/* idea #2 */}
             let Sunday
             {/* end of idea #2 */}
</div>
    )



}

export default Slots
