import React from 'react'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import CardDeck from 'react-bootstrap/CardDeck'
import {Link} from 'react-router-dom'

const Slots = (props) => {
    console.log("this is props", props)

    function dayToString(day_of_week){
        if (day_of_week === 0){
            return "Sunday"
        }else if (day_of_week === 1){
            return "Monday"
        }else if (day_of_week === 2){
            return "Tuesday"
        }else if (day_of_week === 3){
            return "Wednesday"
        }else if (day_of_week === 4){
            return "Thursday"
        }else if (day_of_week == 5){
            return "Friday"
        }else if (day_of_week === 6){
            return "Saturday"
        }
    }

    function timeToString(time_of_day){
        if (time_of_day === 0){
            return "Morning"
        }else if (time_of_day === 1){
            return "Afternoon"
        }else if (time_of_day === 2){
            return "Evening"
        }

    }

    return (
        <div>
            {props.slots && props.slots.map(slot =>
            <div>
                <CardDeck className="card-padding">
            <Card bg="light" style={{ width: '18rem' }}>
            {/* <Card.Header>{id ? id : null}</Card.Header> */}
            <Card.Body>
                <Card.Title>Title</Card.Title>
                <Card.Text>Time</Card.Text>

            </Card.Body>
            </Card>
            
        </CardDeck>
                {dayToString(slot.day_of_week)} - {timeToString(slot.time_of_day)}
            </div>    
            
            )}
        </div>
    )

}

export default Slots
